def exit_gracefully():
    if dev is not None:
        usb.util.dispose_resources(dev)
        if reattach:
            dev.attach_kernel_driver(0)
    print('Done.')

def signal_handler(sig, frame):
    print('You pressed Ctrl+C!')
    exit_gracefully()

signal.signal(signal.SIGINT, signal_handler)


